def get_intent(words):
  for intent_name, intent_data in BOT_CONFIG['intents'].items():
    for example in intent_data['examples']:
      distance = nltk.edit_distance(words.lower(), example.lower())
      if distance / len(example) < 0.4:
        return intent_name

def get_response_by_intent(intent):
  #candidates-responses from gotten intent of intent list
  candidates = BOT_CONFIG['intents'][intent]['responses']
  return random.choice(candidates)

