def get_intent(text):
    for intent_name, intent_date in BOT_CONFIG['intents'].items():
        for example in intent_date['examples']:
            distance = nltk.edit_distance(text.lower(), example.lower())
            if distance / len(example) <= 0.4:
                return intent_name


def get_response_by_intent(intent):
    # ToDo candidates for intent
    candidates = BOT_CONFIG['intents'][intent]['response']
    return random.choice(candidates)


def generate_answer(text):
    return None


def get_default_response():
    candidates = BOT_CONFIG['failure_responses']
    return random.choice(candidates)


def get_answer(text):
    intent = get_intent(text)

    if intent:
        return get_response_by_intent(intent)

    response = generate_answer(text)
    if response:
        return response

    return get_default_response()


